#Load Snap-Ins If($fail -eq $false) { $blnLoaded = Load-Snapins If($blnLoaded -ne $true) {$fail = $true} }
#Connect to vCenter If($fail -eq $false) { $blnConnected = Connect-ToVCenter If($blnConnected -eq $false) {$fail = $true} Else {} }
#create the discovery data object If($fail -eq $false) { #create the discovery data object $discoveryData = $null $discoveryData = $opsmgrAPI.CreateDiscoveryData(0,$sourceID,$managedEntityID) If($discoveryData -eq $null) { $msg = "Could not create Discovery Data." Write-Out $msg 2 $fail = $true } Else {} }
#grab our vCenter objects If($fail -eq $false) { $objVIObjects = $null $objVIObjects = Get-Datacenter If($objVIObjects -eq $null) { $msg = "No VI objects were found." Write-Out $msg 1 $fail = $true } }
#parse our vCenter object properties If($fail -eq $false) { $arrVIObjects = $null If($objVIObjects -is [array]) {$arrVIObjects = $objVIObjects} Else { $arrVIObjects = @() $arrVIObjects += $objVIObjects }
$arrNames = $null $arrNames = @() Foreach($objVIO in $arrVIObjects) { #gather properties $arrProperties = @() $arrProperties += "Id" $arrProperties += "Name" $arrProperties += "Uid"
#gather names for final message $vioName = $null $vioName = $objVIO.Name If($vioName -ne $null) {$arrNames += $vioName} Else { $msg = "Encountered a null VI Object; couldn't read an object's name." Write-Out $msg 2 $fail = $true Break }
#prepare the discovery class instance $strDiscoveredClass = $null $strDiscoveredClass = "$" + "MPElement[Name='" + $gDiscoveredClassName + "']$" $objDiscoveredClass = $null $objDiscoveredClass = $discoveryData.CreateClassInstance($strDiscoveredClass) If($objDiscoveredClass -eq $null) { $msg = "Could not create a class instance for this discovery." Write-Out $msg 2 $fail = $true Break }
#Add the hosting parent's data to the class (required). $strMPElement = $null $strMPElement = "$" + "MPElement[Name='System!System.Entity']/DisplayName" + "$" $objDiscoveredClass.AddProperty($strMPElement,$vioName)
$strMPElement = $null $strMPElement = "$" + "MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName" + "$" $objDiscoveredClass.AddProperty($strMPElement,$vioName)
# #read properties from pshell object and add to discovery data # $Uid = $objVIO.Uid # $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter']/Uid$",$Uid) # # $Id = $objVIO.Id # $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter']/Id$",$Id) # # $Name = $objVIO.Name # $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter']/Name$",$Name)
$arrPropValuePairs = $null $arrPropValuePairs = @() Foreach($property in $arrProperties) { $propValue = $null $propValue = $objVIO.$property If($propValue -ne $null) { $strMPElement = $null $strMPElement = "$" + "MPElement[Name='" + $gDiscoveredClassName + "']/" + $property + "$" $msg = "Writing element: """ + $strMPElement + """." Write-Out $msg $objDiscoveredClass.AddProperty($strMPElement,$propValue) $arrPropValuePairs += $property + " : " + $propValue } Else { $msg = "Could not read the value of the object's property """ + $property + """." Write-Out $msg 1 } } $OFS = " , "; [string]$strPropValuePairs = $arrPropValuePairs; $OFS = " " $msg = "Discovered an object with the following properties: " + $strPropValuePairs Write-Out $msg 1
$discoveryData.AddInstance($objDiscoveredClass) } }
If($fail -eq $false) { $intDiscoveryTotal = $arrNames.Count $OFS = ","; [string]$strNames = $arrNames; $OFS = " " $msg = "Script finished; discovered " + $intDiscoveryTotal + " datacenters with names: """ + $strNames + """ located in the server """ + $vCenterName + """. Returning the discovery data." Write-Out $msg
# [string]$ddata = $opsmgrAPI.Return($discoveryData) # $msg = "Final discovery data: " + $ddata # Write-Out $msg # write-out -f cyan $ddata # $ddata | gm
$discoveryData
