#Add the hosting parent's data to the class (required). $objDiscoveredClass.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$",$vioName) $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter']/Uid$",$dcUID) $objDiscoveredClass.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName$", $vCenterName)
