#Add the "Hosts" sub-class $objDatastores = $null $objDatastores = $discoveryData.CreateClassInstance("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.Hosts']$") $objDatastores.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$","Hosts") $objDatastores.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName$",$vCenterName) $objDatastores.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter']/Uid$",$dcUid) $objDatastores.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster']/Uid$",$clusterUID) $discoveryData.AddInstance($objDatastores) } }
If($fail -eq $false) { $intDiscoveryTotal = $arrNames.Count $OFS = ","; [string]$strNames = $arrNames; $OFS = " " $msg = "Script finished; discovered " + $intDiscoveryTotal + " cluters with names: """ + $strNames + """ located in the server """ + $vCenterName + """. Returning the discovery data." Write-Out $msg
$discoveryData }

