Function Load-vCenterSnapin { $fail = $null $fail = $false $snapin = "VMware.VimAutomation.Core" $msg = "Script is attempting to load snap-in: """ + $snapin + """." Write-Out $msg $snapinTest = $null $snapinTest = Get-PSSnapin $snapin -registered -ea silentlycontinue If($snapinTest -ne $null) { $msg = "The required snap-in is installed on this system: """ + $snapin + """. Adding the snap-in." Write-Out $msg $snapinTest = $null $snapinTest = Get-PSSnapin $snapin -ea silentlycontinue If($snapinTest -eq $null) {$blnAdded = Add-PSSnapin $snapin} Else {} } Else { $fail = $true $msg = "Required Snap-In is not installed on this system: """ + $snapin + """." Write-Out $msg 2 }
