Function Discover-VMNic($objVIO) { #prepare the discovery class instance $objDiscoveredClass = $null $objDiscoveredClass = $discoveryData.CreateClassInstance("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.Host.VMNic']$") If($objDiscoveredClass -eq $null) { $msg = "Could not create a class instance for this discovery." Write-Out $msg 2 $fail = $true Break }
#read properties from pshell object and add to discovery data #key properties of root element $Name = $objVIO.Name $objDiscoveredClass.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$",$Name)
#add host key property
#UID of host (also a key property) [string]$VMHostUid = $objVIO.VMHostUid $arrPairs += ("VMHostUid" + ":" + $VMHostUid) $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.Host.VMNic']/VMHostUid$",$VMHostUid) $strHostUidGuid = $null $strHostUidGuid = Get-HostUIDPropertyGUI $VMHostUid $arrPairs += ("(Host UID GUID) """ + $strHostUidGuid + """:" + $VMHostUid) $objDiscoveredClass.AddProperty($strHostUidGuid,$VMHostUid)
#write-host -f yellow "GUID: $strHostUidGuid"
#other properties $BitRatePerSec = $objVIO.BitRatePerSec $arrPairs += ("BitRatePerSec" + ":" + $BitRatePerSec) $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.Host.VMNic']/BitRatePerSec$",$BitRatePerSec)
$DeviceName = $objVIO.DeviceName $arrPairs += ("DeviceName" + ":" + $DeviceName) $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.Host.VMNic']/DeviceName$",$DeviceName)
$DhcpEnabled = $objVIO.DhcpEnabled $arrPairs += ("DhcpEnabled" + ":" + $DhcpEnabled) $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.Host.VMNic']/DhcpEnabled$",$DhcpEnabled)
$FullDuplex = $objVIO.FullDuplex $arrPairs += ("FullDuplex" + ":" + $FullDuplex) $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.Host.VMNic']/FullDuplex$",$FullDuplex)
$Id = $objVIO.Id $arrPairs += ("Id" + ":" + $Id) $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.Host.VMNic']/Id$",$Id)
