$SubnetMask = $objVIO.SubnetMask $arrPairs += ("SubnetMask" + ":" + $SubnetMask) $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.Host.VMNic']/SubnetMask$",$SubnetMask)
[string]$VMHost = $objVIO.VMHost $arrPairs += ("VMHost" + ":" + $VMHost) $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.Host.VMNic']/VMHost$",$VMHost)
