$WakeOnLanSupported = $objVIO.WakeOnLanSupported $arrPairs += ("WakeOnLanSupported" + ":" + $WakeOnLanSupported) $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.Host.VMNic']/WakeOnLanSupported$",$WakeOnLanSupported)
