$DhcpEnabled = $objVIO.DhcpEnabled $arrPairs += ("DhcpEnabled" + ":" + $DhcpEnabled) $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.Host.VMKernel']/DhcpEnabled$",$DhcpEnabled)
$Id = $objVIO.Id $arrPairs += ("Id" + ":" + $Id) $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.Host.VMKernel']/Id$",$Id)
$FaultToleranceLoggingEnabled = $objVIO.FaultToleranceLoggingEnabled $arrPairs += ("FaultToleranceLoggingEnabled" + ":" + $FaultToleranceLoggingEnabled) $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.Host.VMKernel']/FaultToleranceLoggingEnabled$",$FaultToleranceLoggingEnabled)
$IP = $objVIO.IP $arrPairs += ("IP" + ":" + $IP) $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.Host.VMKernel']/IP$",$IP)
$IPv6 = $objVIO.IPv6 $arrPairs += ("IPv6" + ":" + $IPv6) $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.Host.VMKernel']/IPv6$",$IPv6)
$IPv6Enabled = $objVIO.IPv6Enabled $arrPairs += ("IPv6Enabled" + ":" + $IPv6Enabled) $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.Host.VMKernel']/IPv6Enabled$",$IPv6Enabled)
$IPv6ThroughDHCP = $objVIO.IPv6ThroughDHCP $arrPairs += ("IPv6ThroughDHCP" + ":" + $IPv6ThroughDHCP) $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.Host.VMKernel']/IPv6ThroughDHCP$",$IPv6ThroughDHCP)
$Mac = $objVIO.Mac $arrPairs += ("Mac" + ":" + $Mac) $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.Host.VMKernel']/Mac$",$Mac)
$ManagementTrafficEnabled = $objVIO.ManagementTrafficEnabled $arrPairs += ("ManagementTrafficEnabled" + ":" + $ManagementTrafficEnabled) $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.Host.VMKernel']/ManagementTrafficEnabled$",$ManagementTrafficEnabled)
$Mtu = $objVIO.Mtu $arrPairs += ("Mtu" + ":" + $Mtu) $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.Host.VMKernel']/Mtu$",$Mtu)
$Name = $objVIO.Name $arrPairs += ("Name" + ":" + $Name) $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.Host.VMKernel']/Name$",$Name)
$PortGroupName = $objVIO.PortGroupName $arrPairs += ("PortGroupName" + ":" + $PortGroupName) $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.Host.VMKernel']/PortGroupName$",$PortGroupName)
$SubnetMask = $objVIO.SubnetMask0 $arrPairs += ("SubnetMask" + ":" + $SubnetMask) $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.Host.VMKernel']/SubnetMask$",$SubnetMask)
[string]$VMHost = $objVIO.VMHost $arrPairs += ("VMHost" + ":" + $VMHost) $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.Host.VMKernel']/VMHost$",$VMHost)
$VMHostId = $objVIO.VMHostId $arrPairs += ("VMHostId" + ":" + $VMHostId) $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.Host.VMKernel']/VMHostId$",$VMHostId)
$vMotionEnabled = $objVIO.vMotionEnabled $arrPairs += ("vMotionEnabled" + ":" + $vMotionEnabled) $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.Host.VMKernel']/vMotionEnabled$",$vMotionEnabled)
$OFS = " , "; $strPairs = $arrPairs; $OFS = " " $msg = "Discovered a VMK with the following properties: " + $strPairs Write-Out $msg
Return $objDiscoveredClass }
Function Get-HostUIDPropertyGUI($strHostUID) { $objClassObjects = $null $objClassObjects = Get-MonitoringClass -name "JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.Host" -path "OperationsManagerMonitoring::" | Get-MonitoringObject -path "OperationsManagerMonitoring::"
Foreach($objClassObject in $objClassObjects) { $objUID = $null $prop = $null $prop = "[JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.Host].Uid" $objUID = $objClassObject.$prop $name = $objClassObject.Name
If($objUID -eq $strHostUID) { #write-host -f green "found host!" $objProperty = $objClassObject.GetMonitoringProperties() | Where-Object {$_.Name -eq "Uid"} $strHostUidGuid = $objProperty.Id.Guid #Write-host -f cyan "name: $name`tReadUID: $objUID`tgivenUID: $strHostUID" #Write-host -f cyan "GuidofUid: $strHostUidGuid" } }
Return $strHostUidGuid }
###MAIN LOOP###
#Load vCenter Snap-Ins If($fail -eq $false) { $blnLoaded = Load-vCenterSnapin If($blnLoaded -ne $true) {$fail = $true} }
#Load MOM Snap-Ins If($fail -eq $false) { $blnLoaded = Load-MOMSnapin If($blnLoaded -ne $true) {$fail = $true} }
#Connect to vCenter If($fail -eq $false) { $blnConnected = Connect-ToVCenter If($blnConnected -eq $false) {$fail = $true} Else {} }
$opsmgrRMS = Get-RMSServer If($opsmgrRMS -eq $null -or $opsmgrRMS -eq "") { $msg = "Could not get the RMS server name from the registry." Write-Out $msg $fail = $true }
#connect to mgmt server If($fail -eq $false) { $msg = "Connecting to the management server """ + $opsmgrRMS + """." Write-Out $msg $strConnect = New-ManagementGroupConnection $opsmgrRMS $msg = "Connection result: """ + $strConnect + """." Write-Out $msg If($strConnect -eq "") { $msg = "Failed to connecto the management server: """ + $opsmgrRMS + """." Write-Out $msg 2 $fail = $true } }
#create the discovery data object If($fail -eq $false) { #create the discovery data object $discoveryData = $null $discoveryData = $opsmgrAPI.CreateDiscoveryData(0,$sourceID,$managedEntityID) If($discoveryData -eq $null) { $msg = "Could not create Discovery Data." Write-Out $msg 2 $fail = $true } Else {} }
#grab our vCenter hosts If($fail -eq $false) { $objVIObjects = $null $objVIObjects = Get-VMHost -location $clusterName If($objVIObjects -eq $null) { $msg = "No VI objects were found." Write-Out $msg 1 $fail = $true } }
#parse our vCenter object properties If($fail -eq $false) { $intInterfaces = $null $intInterfaces = 0
$arrVIObjects = $null If($objVIObjects -is [array]) {$arrVIObjects = $objVIObjects} Else { $arrVIObjects = @() $arrVIObjects += $objVIObjects }
$arrNames = $null $arrNames = @() Foreach($objVIO in $arrVIObjects) { #gather names for final message $vioName = $null $vioName = $objVIO.Name If($vioName -ne $null) {$arrNames += $vioName} Else { $msg = "Encountered a null VI Object; couldn't read an object's name." Write-Out $msg 2 $fail = $true Break }
$msg = "Discovering interfaces for host: """ + $vioName + """." Write-Out $msg $arrObjInterfaces = $null $arrObjInterfaces = Get-VMHostNetworkAdapter -vmhost $objVIO Foreach($objInterface in $arrObjInterfaces) { If($fail -eq $false) { $intInterfaces++ #discover the object and add to the discoveryData $strIFName = $null $strIFName = $objInterface.Name $objDiscoveredClass = $null If($strIFName -like "vmnic*") {$objDiscoveredClass = Discover-VMNic $objInterface} ElseIf($strIFName -like "vmk*") {$objDiscoveredClass = Discover-VMK $objInterface} $discoveryData.AddInstance($objDiscoveredClass) } } } }
If($fail -eq $false) { $intDiscoveryTotal = $arrNames.Count $OFS = ","; [string]$strNames = $arrNames; $OFS = " " $msg = "Script finished; discovered """ + $intInterfaces + """ interface(s) for" + $intDiscoveryTotal + " hosts with names: """ + $strNames + """ located in the server """ + $vCenterName + """. Returning the discovery data." Write-Out $msg $msg = "Errors: " + $error Write-Out $msg $discoveryData }

