Param($sourceID,$managedEntityID,$vCenterName,$dcName,$dcUID,$clusterName,$clusterUID)
$error.clear() $fail = $false $scriptName = "Discover-vCenterResourcePools.ps1 b12 (auto) - " $opsmgrAPI = New-Object -comObject 'MOM.ScriptAPI'
#global vars $script:debugLevel = 4 #verbosity $script:blnWriteToScreen = $false #write to the screen $script:blnWriteToOpsMgrLog = $true #write to the opsmgr log
###COMMON FUNCTIONS Function Write-Out($msg,$severity) { $intLevel = 4 If($intLevel -eq $null) {$intLevel = 4}
If($severity -eq $null) {$severity = 0}
If($intLevel -le $script:debugLevel) { If($script:blnWriteToScreen -eq $true) { Switch($severity) { 0 {$color = "white"} 1 {$color = "yellow"} 2 {$color = "magenta"} } Write-Host -f $color $msg } Else{} If($script:blnWriteToOpsMgrLog -eq $true) { If($severity -eq 1){$severity = 2} ElseIf($severity -eq 2){$severity = 1} $opsmgrAPI.LogScriptEvent($scriptName,0,$severity,$msg) } Else{} } }
#parse args $requiredArgs = $null $requiredArgs = @() $requiredArgs += "sourceID" $requiredArgs += "managedEntityID" $requiredArgs += "vCenterName" $requiredArgs += "dcName" $requiredArgs += "dcUID" $requiredArgs += "clusterName" $requiredArgs += "clusterUID"
$failedArgs = $null $failedArgs = @() Foreach($requiredArg in $requiredArgs) { If((Get-Variable $requiredArg -ea silentlycontinue).Value -eq $null) {$failedArgs += $requiredArg} Else {} }
If($failedArgs.Count -gt 0) { $OFS = ","; [string]$strFailedArgs = $failedArgs; $OFS = " " $failedArgs = $null $msg = "The following required arguments are missing: " + $strFailedArgs Write-Out $msg 2 $fail = $true } Else { $arrPairs = $null $arrPairs = @() Foreach($requiredArg in $requiredArgs) { $argValue = $null $argValue = (Get-Variable $requiredArg -ea silentlycontinue).Value $arrPairs += $requiredArg + " : " + $argValue } $OFS = " , "; [string]$strArgPairs = $arrPairs; $OFS = " " $msg = "The following args were passed: " + $strArgPairs Write-Out $msg }
Function Load-Snapins { $fail = $null $fail = $false $snapin = "VMware.VimAutomation.Core" $msg = "Script is attempting to load snap-in: """ + $snapin + """." Write-Out $msg $snapinTest = $null $snapinTest = Get-PSSnapin $snapin -registered -ea silentlycontinue If($snapinTest -ne $null) { $msg = "The required snap-in is installed on this system: """ + $snapin + """. Adding the snap-in." Write-Out $msg $snapinTest = $null $snapinTest = Get-PSSnapin $snapin -ea silentlycontinue If($snapinTest -eq $null) {$blnAdded = Add-PSSnapin $snapin} Else {} } Else { $fail = $true $msg = "Required Snap-In is not installed on this system: """ + $snapin + """." Write-Out $msg 2 }
If($fail -eq $false) { $snapinTest = $null $snapinTest = Get-PSSnapin $snapin if($snapinTest -eq $null) { $fail = $true $msg = "Script didn't complete loading snap-ins; could not add the snapin: """ + $snapin + """." Write-Out $msg 2 } }
If($fail -eq $false) {$retval = $true} Else {$retval = $false} Return $retval }
Function Connect-ToVCenter { $fail = $null $fail = $false
$wprefTmp = $warningPreference $warningPreference = "SilentlyContinue" $blnConnected = Connect-VIServer -server $vCenterName | out-null $warningPreference = $wprefTmp $objDC = $null $objDC = Get-Datacenter If($objDC -eq $null) { $fail = $true $msg = "Could not connect to the vcenter server: """ + $vCenterName + """." Write-Out $msg 2 } Else { $msg = "Connected to the vcenter server: """ + $vCenterName + """." Write-Out $msg }
If($fail -eq $false) {$retval = $true} Else {$retval = $false} Return $retval }
###MAIN LOOP###
#Load Snap-Ins If($fail -eq $false) { $blnLoaded = Load-Snapins If($blnLoaded -ne $true) {$fail = $true} }
#Connect to vCenter If($fail -eq $false) { $blnConnected = Connect-ToVCenter If($blnConnected -eq $false) {$fail = $true} Else {} }
#create the discovery data object If($fail -eq $false) { #create the discovery data object $discoveryData = $null $discoveryData = $opsmgrAPI.CreateDiscoveryData(0,$sourceID,$managedEntityID) If($discoveryData -eq $null) { $msg = "Could not create Discovery Data." Write-Out $msg 2 $fail = $true } Else {} }
#grab our vCenter objects If($fail -eq $false) { $strFilter = $null $strFilter = $dcName $objVIObjects = Get-ResourcePool -location $clusterName If($objVIObjects -eq $null) { $msg = "No VI objects were found." Write-Out $msg 1 $fail = $true } }
#parse our vCenter object properties If($fail -eq $false) { $arrVIObjects = $null If($objVIObjects -is [array]) {$arrVIObjects = $objVIObjects} Else { $arrVIObjects = @() $arrVIObjects += $objVIObjects }
$arrNames = $null $arrNames = @() Foreach($objVIO in $arrVIObjects) { #gather names for final message $vioName = $null $vioName = $objVIO.Name If($vioName -ne $null) {$arrNames += $vioName} Else { $msg = "Encountered a null VI Object; couldn't read an object's name." Write-Out $msg 2 $fail = $true Break }
#prepare the discovery class instance $objDiscoveredClass = $null $objDiscoveredClass = $discoveryData.CreateClassInstance("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.ResourcePool']$") If($objDiscoveredClass -eq $null) { $msg = "Could not create a class instance for this discovery." Write-Out $msg 2 $fail = $true Break }
#Add the hosting parent's data to the class (required). $objDiscoveredClass.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$",$vioName) $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter']/Uid$",$dcUID) $objDiscoveredClass.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName$", $vCenterName) $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster']/Uid$",$clusterUID)
#read properties from pshell object and add to discovery data $Name = $objVIO.Name $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.ResourcePool']/Name$",$Name) $Id = $objVIO.Id $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.ResourcePool']/Id$",$Id) $Uid = $objVIO.Uid $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.ResourcePool']/Uid$",$Uid) $CpuExpandableReservation = $objVIO.CpuExpandableReservation $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.ResourcePool']/CpuExpandableReservation$",$CpuExpandableReservation) $CpuLimitMHz = $objVIO.CpuLimitMHz $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.ResourcePool']/CpuLimitMHz$",$CpuLimitMHz) $CpuReservationMHz = $objVIO.CpuReservationMHz $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.ResourcePool']/CpuReservationMHz$",$CpuReservationMHz) $MemExpandableReservation = $objVIO.MemExpandableReservation $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.ResourcePool']/MemExpandableReservation$",$MemExpandableReservation) $MemLimitMB = $objVIO.MemLimitMB $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.ResourcePool']/MemLimitMB$",$MemLimitMB) $MemReservationMB = $objVIO.MemReservationMB $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.ResourcePool']/MemReservationMB$",$MemReservationMB) $MemSharesLevel = $objVIO.MemSharesLevel $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.ResourcePool']/MemSharesLevel$",$MemSharesLevel) $NumCpuShares = $objVIO.NumCpuShares $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.ResourcePool']/NumCpuShares$",$NumCpuShares) $NumMemShares = $objVIO.NumMemShares $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.ESXCluster.ResourcePool']/NumMemShares$",$NumMemShares)
$discoveryData.AddInstance($objDiscoveredClass) } }
If($fail -eq $false) { $intDiscoveryTotal = $arrNames.Count $OFS = ","; [string]$strNames = $arrNames; $OFS = " " $msg = "Script finished; discovered " + $intDiscoveryTotal + " resource pools with names: """ + $strNames + """ located in the server """ + $vCenterName + """. Returning the discovery data." Write-Out $msg
$discoveryData }

