#Add the hosting parent's data to the class (required).
#read properties from pshell object and add to discovery data $Name = $objVIO.Name $objDiscoveredClass.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$",$Name) $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.Datastore']/Name$",$Name) $Id = $objVIO.Id $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.Datastore']/Id$",$Id) $Uid = $objVIO.Uid $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.Datastore']/Uid$",$Uid) $Accessible = $objVIO.Accessible $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.Datastore']/Accessible$",$Accessible) $CapacityMB = $objVIO.CapacityMB $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.Datastore']/CapacityMB$",$CapacityMB) $FreeSpaceMB = $objVIO.FreeSpaceMB $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.Datastore']/FreeSpaceMB$",$FreeSpaceMB) $Type = $objVIO.Type $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.Datastore']/Type$",$Type) $dcName = $objVIO.datacenter $objDiscoveredClass.AddProperty("$MPElement[Name='JPPacks.VMWare.vCenter.vCenterServer.Datacenter.Datastore']/Datacenter$",$dcName) $discoveryData.AddInstance($objDiscoveredClass)
#Add the relationship $objRelationship = $null $objRelationship = $discoveryData.CreateRelationshipInstance("$MPElement[Name='JPPacks.VMWare.vCenter.Relationships.DatastoresContainsDatastore']$") If($objRelationship -eq $null) { $msg = "Could not create the relationship class." Write-Out $msg 2 } Else { $objRelationship.Source = $objSourceClass $objRelationship.Target = $objDiscoveredClass $discoveryData.AddInstance($objRelationship) } } }
If($fail -eq $false) { $intDiscoveryTotal = $arrNames.Count $OFS = ","; [string]$strNames = $arrNames; $OFS = " " $msg = "Script finished; discovered " + $intDiscoveryTotal + " datastores with names: """ + $strNames + """ located in the server """ + $vCenterName + """. Returning the discovery data." Write-Out $msg
$discoveryData }

Configuration.ScriptBody:
#PerfCol-vCenterHostCPUAvg.ps1

##TODO
